import { useRef, useEffect } from "react";
import './stream.css'
import * as faceapi from 'face-api.js';import '../../face-api.min'
// import {faceapi} from "../../face-api.min";


const Stream = ({videoState, setVideoState}) => {

  const videoRef = useRef();
  const [modelsLoaded, setModelsLoaded] = React.useState(false);
  const [captureVideo, setCaptureVideo] = React.useState(false);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: videoState }).then((stream) => {
      videoRef.current.srcObject = stream;
    });
  }, [videoState]);

  Promise.all([
    faceapi.nets.tnyFaceDetector.loadFromUri('../../models')
  ])
  console.log(faceapi.nets)
  return (
    <div>
      <button onClick={setVideoState}>CLICK</button>
      <div className="video-box center">
        <video ref={videoRef} autoPlay/>
      </div>
    </div>
  );
}

export default Stream;
