import { useRef, useEffect } from "react";
import './stream.css'
import * as faceapi from 'face-api.js';import '../../face-api.min'
// import {faceapi} from "../../face-api.min";


const Stream = ({videoState, setVideoState}) => {

  const videoRef = useRef();
  const [modelsLoaded, setModelsLoaded] = React.useState(false);
  const [captureVideo, setCaptureVideo] = React.useState(false);

  React.useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = process.env.PUBLIC_URL + '../../models';

      romise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        faceapi.nets.faceExpressionNet.loadFromUri(MODEL_URL),
      ]).then(setModelsLoaded(true));
    }
    loadModels();
  }, []);


  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: videoState }).then((stream) => {
      videoRef.current.srcObject = stream;
    });
  }, [videoState]);

  return (
    <div>
      <button onClick={setVideoState}>CLICK</button>
      <div className="video-box center">
        <video ref={videoRef} autoPlay/>
      </div>
    </div>
  );
}

export default Stream;
